public class MainInt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		byte b = 123;
		System.out.println("Vrednost promenljive b je "+b);
		
		short s = 12345;
		System.out.println("Vrednost promenljive s je "+s);
		
		int i = 1234567891;
		System.out.println("Vrednost promenljive i je "+i);
		
		long l = 1234567891234567891L;
		System.out.println("Vrednost promeljive l je "+l);
		
		long ll = 1234_5678_9012_12L;
		System.out.println("Vrednost promeljive ll je "+ll);
		
		//
		byte b1 = 120; 
		short s1 = 5200; 
		int i1 = 100200; 
		long l1 = 18000000000L; 
		System.out.println("\nByte " + b1); 
		System.out.println("Short " + s1); 
		System.out.println("Int " + i1); 
		System.out.println("Long " + l1);
		
		
	}

}
